import React, { useState } from 'react';

const ClipboardItem = ({ clip, onCopy, onEdit, onDelete }) => {
  const [copied, setCopied] = useState(false);

  const handleCopy = () => {
    onCopy(clip);
    setCopied(true);
    setTimeout(() => setCopied(false), 2000);
  };

  const isImage = clip.content && clip.content.startsWith('data:image/');
  const createdDate = new Date(clip.createdAt).toLocaleDateString('en-US', {
    month: 'short',
    day: 'numeric',
    year: 'numeric',
    hour: '2-digit',
    minute: '2-digit'
  });
  const isEdited = clip.updatedAt && clip.updatedAt !== clip.createdAt;

  return (
    <div className="clipboard-item">
      <div className="item-header">
        <h3>{clip.title}</h3>
        {clip.category && (
          <span className="category-badge">{clip.category}</span>
        )}
      </div>

      <div className="item-content">
        {isImage ? (
          <div className="image-content">
            <img src={clip.content} alt={clip.title} />
          </div>
        ) : (
          <div className="text-content">{clip.content}</div>
        )}
      </div>

      {clip.tags && clip.tags.length > 0 && (
        <div className="item-tags">
          {clip.tags.map(tag => (
            <span key={tag} className="tag">
              üè∑Ô∏è {tag}
            </span>
          ))}
        </div>
      )}

      <div className="item-footer">
        <div className="item-date">
          {createdDate}
          {isEdited && <span className="edited-badge">(edited)</span>}
        </div>
        <div className="item-actions">
          <button onClick={handleCopy} className="btn-copy">
            {copied ? '‚úì Copied!' : 'üìã Copy'}
          </button>
          <button onClick={() => onEdit(clip)} className="btn-edit">
            ‚úèÔ∏è Edit
          </button>
          <button onClick={() => onDelete(clip.id)} className="btn-delete">
            üóëÔ∏è Delete
          </button>
        </div>
      </div>
    </div>
  );
};

export default ClipboardItem;
