import React, { useState, useEffect } from 'react';

const ClipboardForm = ({ clip, onSubmit, onCancel, existingTags, existingCategories }) => {
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [tags, setTags] = useState([]);
  const [category, setCategory] = useState('');
  const [tagInput, setTagInput] = useState('');
  const [imagePreview, setImagePreview] = useState(null);
  const [isImage, setIsImage] = useState(false);

  useEffect(() => {
    if (clip) {
      setTitle(clip.title || '');
      setContent(clip.content || '');
      setTags(clip.tags || []);
      setCategory(clip.category || '');
      if (clip.content && clip.content.startsWith('data:image/')) {
        setIsImage(true);
        setImagePreview(clip.content);
      }
    }
  }, [clip]);

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file && file.type.startsWith('image/')) {
      const reader = new FileReader();
      reader.onload = (event) => {
        setContent(event.target.result);
        setImagePreview(event.target.result);
        setIsImage(true);
      };
      reader.readAsDataURL(file);
    }
  };

  const handlePaste = async (e) => {
    e.preventDefault();
    const text = e.clipboardData.getData('text');
    if (text) {
      setContent(text);
      setIsImage(false);
      setImagePreview(null);
      return;
    }

    const items = e.clipboardData.items;
    for (let item of items) {
      if (item.type.startsWith('image/')) {
        const file = item.getAsFile();
        const reader = new FileReader();
        reader.onload = (event) => {
          setContent(event.target.result);
          setImagePreview(event.target.result);
          setIsImage(true);
        };
        reader.readAsDataURL(file);
        return;
      }
    }
  };

  const handleAddTag = () => {
    if (tagInput.trim() && !tags.includes(tagInput.trim())) {
      setTags([...tags, tagInput.trim()]);
      setTagInput('');
    }
  };

  const handleRemoveTag = (tagToRemove) => {
    setTags(tags.filter(tag => tag !== tagToRemove));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!title.trim() || !content.trim()) {
      alert('Title and content are required');
      return;
    }

    const clipData = {
      title: title.trim(),
      content: content.trim(),
      tags,
      category: category.trim()
    };

    if (clip) {
      clipData.id = clip.id;
    }

    onSubmit(clipData);
  };

  return (
    <form onSubmit={handleSubmit} className="clipboard-form">
      <h2>{clip ? '‚úèÔ∏è Edit Clip' : '‚ûï Add New Clip'}</h2>

      <div className="form-group">
        <label htmlFor="title">Title *</label>
        <input
          id="title"
          type="text"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          placeholder="Enter clip title"
          required
        />
      </div>

      <div className="form-group">
        <label htmlFor="content">Content *</label>
        <textarea
          id="content"
          value={isImage ? '' : content}
          onChange={(e) => {
            setContent(e.target.value);
            setIsImage(false);
            setImagePreview(null);
          }}
          onPaste={handlePaste}
          placeholder="Paste text or image here (Ctrl+V / Cmd+V)"
          rows="6"
          disabled={isImage}
        />
        {!isImage && (
          <div className="upload-section">
            <label htmlFor="image-upload" className="upload-label">
              üì∑ Or upload image
            </label>
            <input
              id="image-upload"
              type="file"
              accept="image/*"
              onChange={handleImageUpload}
              style={{ display: 'none' }}
            />
          </div>
        )}
        {imagePreview && (
          <div className="image-preview">
            <img src={imagePreview} alt="Preview" />
            <button
              type="button"
              className="btn-remove-image"
              onClick={() => {
                setContent('');
                setImagePreview(null);
                setIsImage(false);
              }}
            >
              ‚ùå Remove Image
            </button>
          </div>
        )}
      </div>

      <div className="form-group">
        <label htmlFor="category">Category</label>
        <input
          id="category"
          type="text"
          value={category}
          onChange={(e) => setCategory(e.target.value)}
          placeholder="e.g., Work, Personal, Code"
          list="category-suggestions"
        />
        <datalist id="category-suggestions">
          {existingCategories.map(cat => (
            <option key={cat} value={cat} />
          ))}
        </datalist>
      </div>

      <div className="form-group">
        <label htmlFor="tags">Tags</label>
        <div className="tag-input-container">
          <input
            id="tags"
            type="text"
            value={tagInput}
            onChange={(e) => setTagInput(e.target.value)}
            onKeyPress={(e) => {
              if (e.key === 'Enter') {
                e.preventDefault();
                handleAddTag();
              }
            }}
            placeholder="Add tags"
            list="tag-suggestions"
          />
          <button type="button" onClick={handleAddTag} className="btn-add-tag">
            Add
          </button>
        </div>
        <datalist id="tag-suggestions">
          {existingTags.map(tag => (
            <option key={tag} value={tag} />
          ))}
        </datalist>
        {tags.length > 0 && (
          <div className="tags-list">
            {tags.map(tag => (
              <span key={tag} className="tag">
                {tag}
                <button
                  type="button"
                  onClick={() => handleRemoveTag(tag)}
                  className="tag-remove"
                >
                  √ó
                </button>
              </span>
            ))}
          </div>
        )}
      </div>

      <div className="form-actions">
        <button type="button" onClick={onCancel} className="btn-cancel">
          Cancel
        </button>
        <button type="submit" className="btn-submit">
          {clip ? 'üíæ Update' : '‚ûï Add Clip'}
        </button>
      </div>
    </form>
  );
};

export default ClipboardForm;
